import "../css/styles.css"

const handleLogin = () => {
  window.location.href = "http://localhost:5001/api/auth/login";
};


const LoginPage = () => {
  return (
    <div className="login-page" style={{
      backgroundImage: `url("/spotify_logo.png")`,  // ✅ Correct inline style
      backgroundSize: "cover",
      backgroundPosition: "center",
      height: "100vh", 
      width: "100vw"
    }}>
      <h1 className="login-header">Stringify</h1>
      <a onClick={handleLogin} className="button-container" >
        <button className="login-button">Login with Spotify</button>
        <img src="/spotify_logo.png" alt="Spotify Logo" className="button-image" />
      </a>
    </div>
  );
};

export default LoginPage;
